import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import PromotionIcon from '@material-ui/icons/List';
import RewardsIcon from '@material-ui/icons/Star';
import HomeIcon from '@material-ui/icons/Store';
import CouponIcon from '@material-ui/icons/CardGiftcard';
import CartIcon from '@material-ui/icons/ShoppingCart';
// import useWindowSize from './use-window-size';

const useStyles = makeStyles(theme => ({
  BottomNavBar: {
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
}));

export default function SimpleBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState(2);

  return (
    <BottomNavigation
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.BottomNavBar}
    >
      <BottomNavigationAction label="Recents" icon={<PromotionIcon />} />
      <BottomNavigationAction label="Rewards" icon={<RewardsIcon />} />
      <BottomNavigationAction label="Menu" icon={<HomeIcon />} />
      <BottomNavigationAction label="Coupon" icon={<CouponIcon />} />
      <BottomNavigationAction label="Cart" icon={<CartIcon />} />
    </BottomNavigation>
  );
}
